sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
    var obvUtil = new OracleBootVolumeUtils();
    obvUtil.validateInputVariables(inputs, outputs);
    obvUtil.validateMandatoryVariables(inputs,["bootVolumeId"]);
    var payload = obvUtil.createPayload(inputs,["displayName","isAutoTuneEnabled","sizeInGBs","vpusPerGB"]);
    obvUtil.trimValues(inputs.freeformTags);
    var tags = obvUtil.createTagsPayload(inputs.freeformTags);  
    if(Object.keys(tags).length){
          payload.freeformTags = tags;
    }
  	outputs.payload = JSON.stringify(payload);
 	outputs.bootvolumeid = inputs.bootVolumeId;

  }
  catch(ex){

    throw new Error("Mandatory fields should be provided");

  }
})(inputs, outputs);

