sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var obvUtil = new OracleBootVolumeUtils();
  obvUtil.validateInputVariables(inputs, outputs);
  obvUtil.validateMandatoryVariables(inputs,["boot_volume_attachment_id"]);
  outputs.boot_volume_attachment_id = inputs.boot_volume_attachment_id;
  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

