sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
    var obvUtil = new OracleBootVolumeUtils();
    obvUtil.validateInputVariables(inputs, outputs);
    obvUtil.validateMandatoryVariables(inputs,["bootVolumeBackupId"]);
    outputs.boot_volume_backup_id = inputs.bootVolumeBackupId;
  }
  catch(ex){

    throw new Error("Mandatory fields should be provided");

  }
})(inputs, outputs);

