 	var responseData = JSON.parse(inputs.response);
    outputs.definedtags = responseData.definedTags;
    outputs.systemtags = responseData.systemTags;
    outputs.displayname = responseData.displayName;
    outputs.freeformtags = responseData.freeformTags;
    outputs.bootvolumeid = responseData.bootVolumeId;
    outputs.imageid = responseData.imageId;
    outputs.lifecyclestate = responseData.lifecycleState;
    outputs.sizeingbs = responseData.sizeInGBs;
    outputs.sourcetype = responseData.sourceType;
    outputs.sourcedetails = responseData.sourceDetails;
    outputs.timecreated = responseData.timeCreated;
    outputs.timeRequestReceived = responseData.timeRequestReceived;
    outputs.kmskeyid = responseData.kmsKeyId;
    outputs.type = responseData.type;
    outputs.uniqueSizeInGBs = responseData.uniqueSizeInGBs;
    outputs.sourceBootVolumeBackupId = responseData.sourceBootVolumeBackupId;
    outputs.status = responseData.status;
  }
  catch(ex){
	throw new Error('JSON Parse Error. Response Body may be empty.');
  }
}
 else {
       	var errorData = new OracleBootVolume_ErrorHandling().errorHandler(inputs.response);
   		outputs.errormessage = errorData[0];
        outputs.status = errorData[1];
    }
})(inputs, outputs);

