sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var obvUtil = new OracleBootVolumeUtils();
  obvUtil.validateInputVariables(inputs, outputs);
  obvUtil.validateMandatoryVariables(inputs,["bootVolumeId"]);
  outputs.boot_volume_id = inputs.bootVolumeId;
  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

