sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var obvUtil = new OracleBootVolumeUtils();
  obvUtil.validateInputVariables(inputs, outputs);
  obvUtil.validateMandatoryVariables(inputs,["tenancyID"]);
     if(inputs.tenancyID && inputs.compartmentID){
		outputs.compartmentid = inputs.compartmentID;
    }
    else if(inputs.tenancyID){
    	outputs.compartmentid = inputs.tenancyID;
    }

  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

