sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var data = new Array();
    var document;
    var documents = new Object();

    if (inputs.status == '200') {
        try {
            var responseData = JSON.parse(inputs.response);
          	outputs.boot_volume_detailed_list = responseData;
            for (var i = 0; i < responseData.length; i++) {
                var document = new Object();
                document.label = responseData[i].displayName;
                document.name = responseData[i].id;
                data.push(document);
            }
        } catch (ex) {
            var dummyDoc = new Object();
            dummyDoc.label = '<No Boot Volume Retrieved>';
            dummyDoc.name = null;
            data.push(dummyDoc);
        } finally {
            documents.data = data;
            outputs.documents = documents;
            outputs.status = "Success";
        }
    } 
  else {
        var errMsgs = new OracleBootVolume_ErrorHandling().errorHandler(inputs.response);
        outputs.error_message = errMsgs[0];
        outputs.status = errMsgs[1];
    }
})(inputs, outputs);

